my @outputList;

#runs through every kmer
for(my $i = 0; $i < scalar @codons; $i++) {
	my $currentKMer = $codons[$i]; #gets the kmer
	my $lenOfFix = length($currentKMer) - 1;
	my $suffix = substr($currentKMer,(-1 * $lenOfFix)); #finds suffix of current one
	#for every kmer again
	for(my $j = 0; $j < scalar @codons; $j++) {
		if ($j == $i) {
			next;
		}
		my $newKMer = $codons[$j];
		my $prefix = substr($newKMer,0,$lenOfFix); #gets the prefix of new kmer
		if($prefix eq $suffix) { #if they match, adds to output
			my $outputS = $currentKMer." -> ".$newKMer;
			push @outputList,$outputS;
		}
	}
}
#orders the list
@outputList = reverse(sort(@outputList));
#prints out results
while(@outputList) {
	my $current = pop @outputList;
	print $current,"\n";
}
