#!/usr/bin/perl
#Sean Wagner
#BA3E.pl
#determines the de bruijn graph of a list of kmers using substrings and stuff

use List::MoreUtils qw(uniq);

#method to determine the index in the array to deal with
sub getArrIndex { #value to search, array of all terms
  my $valToSearch = shift;
  #print "val search: ",$valToSearch,"\n";
  my @arr = @{$_[0]};
  for(my $i = 0; $i < scalar @arr; $i++) {
    my $curVal = $arr[$i];
    #print "curr val: ",$curVal,"\n";
    if($curVal eq $valToSearch) {
      #print "RETURN: ",$i,"\n";
      return $i;
    }
  }
  return -1;
}

