my @outputArr;
#needs to find the connections between all the kmers

#SETUP
my @arrList;
#find every possible prefix and suffix
for(my $i = 0; $i < scalar @codons; $i++) {
  my $currentK = $codons[$i];
  my $lenOfFix = length($currentK) - 1;
  my $prefix = substr($currentK,0,$lenOfFix);
  my $suffix = substr($currentK,(-1 * $lenOfFix));
  #add them to a list
  push @arrList, $prefix;
  push @arrList, $suffix;
}
#sort the list
@arrList = uniq @arrList;
@arrList = reverse(sort(@arrList));
#use list to determine array indexes going forward

#Setup the array with value of 0 in all desired spots
my $lenList = scalar @arrList;
for(my $i = 0; $i < $lenList; $i++) {
  for(my $j = 0; $j < $lenList; $j++) {
    $arrList[$i][$j] = 0;
  }
