#SETUP COMPLETED

for(my $i = 0; $i < scalar @codons; $i++) {
  my $currentK = $codons[$i];
  my $lenOfFix = length($currentK) - 1;
  #find the prefix
  my $prefix = substr($currentK,0,$lenOfFix);
  #find suffix
  my $suffix = substr($currentK,(-1 * $lenOfFix));
  #for every kmer again
  my $preIn = getArrIndex($prefix,\@arrList);
  my $sufIn = getArrIndex($suffix,\@arrList);
  my $newValToStore = $outputArr[$preIn][$sufIn] + 1;
  $outputArr[$preIn][$sufIn] = $newValToStore;
}

#for every kmer
for(my $i = 0; $i < scalar @codons; $i++) {
  my $currentK = $codons[$i];
  my $lenOfFix = length($currentK) - 1;
  #find the prefix
  my $prefix = substr($currentK,0,$lenOfFix);
  #find suffix
  my $suffix = substr($currentK,(-1 * $lenOfFix));
  #for every kmer again
  my $preIn = getArrIndex($prefix,\@arrList);
  my $sufIn = getArrIndex($suffix,\@arrList);
  for(my $j = 0; $j < scalar @codons; $j++) {
    my $newK = $codons[$j];
    my $lenOfFix = length($newK) - 1;
    #find the prefix
    my $newPre = substr($newK,0,$lenOfFix);
    #find suffix
    my $newSuf = substr($newK,(-1 * $lenOfFix));
    #if there is a match, draw the match on the graph and increment count
    #if suffix matches prefix of other word, map the suffix of a to suffix of b
    if($suffix eq $newPre) {
      my $preIndex = getArrIndex($suffix,\@arrList);
      my $sufIndex = getArrIndex($newSuf,\@arrList);
      my $currentCountGraph = $outputArr[$preIndex][$sufIndex];
      if($currentCountGraph == 0) {
